clc; close all; clear all;

in{1} = '';
in{2} = '';
in{3} = '';
in{4} = '';
in{5} = '';

%     1
%   2 3 4
% 5 6 7 8 9
%   A B C
%     D
%
% is equivalent to:
%             +2i
%     -1+1i   +1i   1+1i
% -2   -1      0     +1    +2 
%     -1-1i   -1i   1-1i
%             -2i
%

    


% allocation of c ... to save result
c = zeros(1,5);
for l=1:length(c)
    v = in{l}; % row of 'ULU...'
    position = -2;   % starting position at 5 (-2 in complex plain)
    for k=1:length(v)
        % translate 'U','D','L','R' to complex numbers
        switch v(k)
            case 'U'
                new = 1i;
            case 'D'
                new = -1i;
            case 'L'
                new = -1;
            case 'R'
                new = 1;
        end
        if abs(position + new) < 2.1 % to not exceed the pannel
            position = position + new;
        end
    end
    c(l) = position;
end

